// Parse the HTML to extract grant links
const html = $input.first().json.data || $input.first().json.body || '';

// Extract grant IDs and basic info from the list page
const grantPattern = /Go\/Show\?GoUuid=([a-f0-9-]+)/gi;
const grants = [];
let match;

while ((match = grantPattern.exec(html)) !== null) {
  grants.push({
    uuid: match[1],
    url: `https://www.grants.gov.au/Go/Show?GoUuid=${match[1]}`
  });
}

// Deduplicate
const unique = [...new Map(grants.map(g => [g.uuid, g])).values()];

return unique.slice(0, 50).map(g => ({ json: g })); // Limit to 50 per run

// Parse grant detail page HTML
const html = $input.first().json.data || $input.first().json.body || '';
const url = $input.first().json.url || '';
const uuid = url.match(/GoUuid=([a-f0-9-]+)/i)?.[1] || '';

// Helper to extract text between patterns
function extractBetween(html, startPattern, endPattern) {
  const startMatch = html.match(startPattern);
  if (!startMatch) return null;
  const startIndex = startMatch.index + startMatch[0].length;
  const endMatch = html.slice(startIndex).match(endPattern);
  if (!endMatch) return null;
  return html.slice(startIndex, startIndex + endMatch.index).trim();
}

// Helper to clean HTML
function cleanHtml(str) {
  if (!str) return '';
  return str
    .replace(/<[^>]+>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/\s+/g, ' ')
    .trim();
}

// Extract fields
const title = cleanHtml(extractBetween(html, /<h1[^>]*>/i, /<\/h1>/i)) || 
              cleanHtml(extractBetween(html, /class="go-title"[^>]*>/i, /<\//i)) || 
              'Unknown Grant';

const goId = extractBetween(html, /GO ID[:\s]*/i, /[<\n]/) ||
             extractBetween(html, /goId["']?\s*:\s*["']?/i, /["'<,]/);

const agency = cleanHtml(extractBetween(html, /Agency[:\s]*/i, /<\//i)) ||
              cleanHtml(extractBetween(html, /Department[:\s]*/i, /<\//i));

const description = cleanHtml(extractBetween(html, /Description[:\s]*<[^>]+>/i, /<\/div>/i)) ||
                   cleanHtml(extractBetween(html, /class="description"[^>]*>/i, /<\//i));

const closeDate = extractBetween(html, /Close Date[^:]*:[\s]*/i, /[<\n]/) ||
                  extractBetween(html, /Closing[^:]*:[\s]*/i, /[<\n]/);

const category = cleanHtml(extractBetween(html, /Category[:\s]*/i, /<\//i)) ||
                cleanHtml(extractBetween(html, /Primary Category[:\s]*/i, /<\//i));

const fundingText = extractBetween(html, /Funding[^:]*:[\s]*/i, /[<\n]/) ||
                    extractBetween(html, /Value[^:]*:[\s]*/i, /[<\n]/) || '';

// Parse funding amounts
const fundingMatch = fundingText.match(/\$?([\d,]+)(?:\s*-\s*\$?([\d,]+))?/i);
const fundingMin = fundingMatch ? parseInt(fundingMatch[1].replace(/,/g, ''), 10) : null;
const fundingMax = fundingMatch && fundingMatch[2] ? 
                   parseInt(fundingMatch[2].replace(/,/g, ''), 10) : fundingMin;

// Parse close date
function parseDate(dateStr) {
  if (!dateStr || /ongoing/i.test(dateStr)) return null;
  try {
    const date = new Date(dateStr);
    return isNaN(date.getTime()) ? null : date.toISOString().split('T')[0];
  } catch {
    return null;
  }
}

const grant = {
  external_id: goId || uuid,
  source: 'grantconnect',
  source_url: `https://www.grants.gov.au/Go/Show?GoUuid=${uuid}`,
  title: title,
  description: description.slice(0, 2000),
  provider: agency,
  provider_type: 'federal',
  funding_amount_min: fundingMin,
  funding_amount_max: fundingMax,
  funding_type: 'grant',
  close_date: parseDate(closeDate),
  status: /ongoing/i.test(closeDate) ? 'ongoing' : 'open',
  is_ongoing: /ongoing/i.test(closeDate),
  category: category || 'General',
  states: ['national'],
  last_scraped_at: new Date().toISOString()
};

return [{ json: grant }];
